    public function getHeaders($to){


/*
				SELECT u.ID_USER, c.ID_CONVERSATION, u.NICKNAME 
            	FROM conversations c, users u
            	WHERE CASE
            	WHEN c.USER_1_ID = '".$_SESSION['id']."'
