//====================================================================== REG

       public function reg($name, $lastname, $nickname, $email, $pass, $rank, $city_id){

       			$pass_sha1 = sha1($pass);

	            $Users = new Users();
	            $Users->NAME = $name;
	            $Users->LASTNAME = $lastname;
	            $Users->NICKNAME = $nickname;
	            $Users->EMAIL = $email;
	            $Users->PASSWORD = $pass_sha1;
	            $Users->RANK = $rank;
	            $Users->CITY_ID = $city_id;
	            $Users->TOKEN = 0;
	            $Users->save();
	           
	            return $Users->toArray();
	   }

